$(document).scroll(function(){var a=$(this).scrollTop();if(a>280){$(".alertbar").fadeIn()}else{$(".alertbar").fadeOut()}});var didScroll;var lastScrollTop=0;var delta=5;var navbarHeight=$("nav").outerHeight();$(window).scroll(function(a){didScroll=true});setInterval(function(){if(didScroll){hasScrolled();didScroll=false}},250);function hasScrolled(){var a=$(this).scrollTop();if(Math.abs(lastScrollTop-a)<=delta){return}if(a>lastScrollTop&&a>navbarHeight){$("nav").removeClass("nav-down").addClass("nav-up");$(".nav-up").css("top",-$("nav").outerHeight()+"px")}else{if(a+$(window).height()<$(document).height()){$("nav").removeClass("nav-up").addClass("nav-down");$(".nav-up, .nav-down").css("top","0px")}}lastScrollTop=a};